if(typeof openTinyMceMedia == 'function'){

  tinymce.PluginManager.add('storeimage', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('storeimage', {
      tooltip: 'Image from Storage',
      icon: 'image',
      onclick: function() {
        openTinyMceMedia(editor);
      }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('storeimage', {
      icon : 'image',
      text: 'Image from Storage',
      context: 'insert',
      onclick: function() {
        openTinyMceMedia(editor);
      }
    });

    return {
      getMetadata: function () {
        return  {
          name: "Store Image Plugin",
          url: "https://tianrosandhy.com"
        };
      }
    };
  });

}
